class DataHelper{static getFormData(e){return[].slice.call(e.querySelectorAll("input, select")).reduce(((e,t)=>Object.assign(e,{[t.id]:t.value})),{})}static getQueryData(e=window.location,t=!1){const a=Array.from(new URL(e).searchParams.entries()).reduce(((e,[t,a])=>Object.assign(e,{[t]:a})),{});return t?Object.keys(a).reduce(((e,t)=>Object.assign(e,{[t.replace(/\-/g,"_")]:a[t]})),{}):a}}